{
   var handle = Isolate.Fake.AllInstances<Dependency>();
   ClassUnderTest.AddCheckingTwoInternalDependencies(1, 2);

   var instance2 = Isolate.Verify.GetInstancesOf(fake)[1];

   Assert.AreEqual(2, instance2.field);
}
