public void VerifyMethodWasNotCalled()
{
   RealLogger fake = Isolate.Fake.Instance<RealLogger>();

   fake.Increment();

   // This verification should fail
   Isolate.Verify.WasNotCalled(() => fake.Increment()); 
}

